import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { socket } from "../../../../Services/ApiServices/SocketIOClient/Socket.IOClient";
import { useAuth } from "../../../../Services/CustomHooks/AuthProvider";
import { setDateFormat } from "../../../ComponentTSCode/CommonComponentTSCode";

const ChatUINavComponent: any = ({ ...props }) => {
    const [notifications, setNotifications] = useState([]);

    let LogOut: any = useAuth()[2];
    let navigate: any = useNavigate();

    useEffect(() => {
        const controller = new AbortController();

        if (notifications.length === 0 && props.user != null) getNotifications(props.user.Id, setNotifications);

        return () => {
            controller.abort();
            LogOut = null;
            navigate = null;
        };
    }, []);

    //getting notifications from api
    const getNotifications: any = async (id: string, setState: any) => {
        setState(await props.http.get(`/notifications/${id}`));
    };
    
    //renders begin
    const showNotifications: Function = () => {
        if (notifications.length === 0) {
            return (
                <li>
                    <a className="block text-center py-2 px-4 rounded-lg text-gray-700 hover:bg-indigo-600 hover:text-white dark:text-gray-200 ">
                        There are no notifications to show
                    </a>
                </li>
            );
        }

        return notifications.map((not: any) =>
            <li key={not._id}>
                <a className="flex text-sm text-justify rounded-lg items-start py-2 px-3 text-gray-700 hover:bg-indigo-600 hover:text-white dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">
                    <img className="w-14 h-auto mr-2 rounded-full" src="data:image/jpeg;base64," alt="user photo" />
                    <div className="flex" style={{ flexDirection: 'column' }}>
                        <div className=""><strong>{not.UserName}</strong> {not.Body}</div>
                        <div className="">{setDateFormat(not.SendDate, 'notifications')}</div>
                    </div>
                </a>
            </li>
        );
    };
    
    if (props.user === null) return <div></div>;
    //renders end

    return (
        <nav className="bg-indigo-600 text-white border-gray-200 px-2 sm:px-4 py-2 dark:bg-gray-800 ">
            <div className="container flex flex-wrap justify-between items-center mx-auto">
                <a href="#" className="flex items-center">
                    {/**<img src="/docs/images/logo.svg" className="mr-3 h-6 sm:h-10" alt="Chat App Logo" /> */}
                    <span className="self-center text-xl font-semibold whitespace-nowrap dark:text-white">Chat App</span>
                </a>
                <div className="flex items-center md:order-2">

                    {/*Notifications dropdown */}
                    <div className="dropdown dropdown-end mr-2">
                        <button className="">
                            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"></path></svg>
                            {/*<div className="indicator">
                                <span className="badge badge-xs badge-primary indicator-item bg-white"></span>
                            </div>*/}
                        </button>
                        <ul tabIndex={0} className="dropdown-content menu shadow bg-base-100 rounded-lg w-[400px] p-2">
                            <li>
                                <a className="block text-xl py-2 px-4 text-black cursor-auto hover:bg-white dark:text-gray-200 dark:hover:text-white">
                                    Notifications
                                </a>
                            </li>
                            {showNotifications()}
                        </ul>
                    </div>

                    {/* User options dropdown */}
                    <div className="dropdown dropdown-end mr-2">
                        <button className="">
                            <img className="w-8 h-8 rounded-full" src="data:image/jpeg;base64," alt="user photo" />

                        </button>
                        <ul tabIndex={0} className="dropdown-content menu shadow bg-base-100 rounded-lg w-[250px] p-2">
                            <li>
                                <a className="block py-2 px-4 text-black cursor-auto hover:bg-white dark:text-gray-200 dark:hover:text-white">
                                    <span className="block text-gray-900 dark:text-white">{`${props.user.Name} ${props.user.LName}(${props.user.UserName})`}</span>
                                    <span className="block font-medium text-sm text-gray-500 truncate dark:text-gray-400">{props.user.Email}</span>
                                </a>
                            </li>
                            <li>
                                <a type="button" onClick={() => LogOut().then((data: any) => {
                                    if (data) {
                                        socket.emit('User unauthenticated', props.user.Id, new Date());
                                        navigate("/signIn");
                                    }
                                })} className="cursor-pointer rounded-lg block py-2 px-4 text-gray-700 hover:bg-red-700 hover:text-white dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Sign out</a>
                            </li>
                        </ul>
                    </div>
                    {/*<button data-collapse-toggle="mobile-menu-2" type="button" className="inline-flex items-center p-2 ml-1 text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="mobile-menu-2" aria-expanded="false">
                        <span className="sr-only">Open main menu</span>
                        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M3 5a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 15a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd"></path></svg>
                        <svg className="hidden w-6 h-6" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"></path></svg>
                    </button>*/}
                </div>
                {/*<div className="hidden justify-between items-center w-full md:flex md:w-auto md:order-1" id="mobile-menu-2">
                    <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md md:font-medium">
                        <li>
                            <a href="#" className="block py-2 pr-4 pl-3 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0 dark:text-white" aria-current="page">Home</a>
                        </li>

                    </ul>
                </div>*/}
            </div>
        </nav>
    );
};

export default ChatUINavComponent;